import os
import random
import string

# Function to generate a random username
def generate_username():
    return ''.join(random.choices(string.ascii_lowercase, k=8))

# Function to generate a random password
def generate_password():
    return ''.join(random.choices(string.ascii_letters + string.digits, k=12))

# Function to simulate data extraction
def extract_data():
    # Simulate extracting user data
    users = {
        generate_username(): {
            'password': generate_password(),
            'email': f'{generate_username()}@example.com',
            'phone': f'123-{random.randint(100, 999)}-{random.randint(1000, 9999)}'
        }
        for _ in range(10)
    }

    # Simulate extracting system data
    system_data = {
        'os': 'Windows 10',
        'cpu': 'Intel Core i7',
        'ram': '16 GB'
    }

    return users, system_data

# Function to print the extracted data
def print_data(users, system_data):
    print("Extracted User Data:")
    for username, user_info in users.items():
        print(f"Username: {username}")
        print(f"Password: {user_info['password']}")
        print(f"Email: {user_info['email']}")
        print(f"Phone: {user_info['phone']}\n")

    print("Extracted System Data:")
    for key, value in system_data.items():
        print(f"{key}: {value}")

# Main function
def main():
    users, system_data = extract_data()
    print_data(users, system_data)

if __name__ == "__main__":
    main()
```

